import axios from 'axios';
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const API_URL = 'http://localhost:3001/api';
const GASOLINERAS = ['UNO', 'TEXACO', 'SHELL', 'ESSO', 'PUMA'];

function App() {
  const [usuario, setUsuario] = useState(null);
  const [tabActiva, setTabActiva] = useState('capturador');
  const [vehiculos, setVehiculos] = useState([]);
  const [registros, setRegistros] = useState([]);
  const [estadisticasGenerales, setEstadisticasGenerales] = useState(null);
  const [estadisticasVehiculos, setEstadisticasVehiculos] = useState([]);

  const [formData, setFormData] = useState({
    vehiculo_id: '',
    fecha: new Date().toISOString().split('T')[0],
    kilometraje_inicial: '',
    kilometraje_final: '',
    costo_litro: '',
    pago_total: '',
    gasolinera: 'UNO'
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (usuario) {
      cargarVehiculos();
      cargarRegistros();
      cargarEstadisticas();
    }
  }, [usuario]);

  const cargarVehiculos = async () => {
    try {
      const response = await axios.get(`${API_URL}/vehiculos`);
      setVehiculos(response.data);
      if (response.data.length > 0 && !formData.vehiculo_id) {
        setFormData(prev => ({ ...prev, vehiculo_id: response.data[0].id }));
      }
    } catch (error) {
      console.error('Error cargando vehículos:', error);
    }
  };

  const cargarRegistros = async () => {
    try {
      const response = await axios.get(`${API_URL}/registros?limit=50`);
      setRegistros(response.data);
    } catch (error) {
      console.error('Error cargando registros:', error);
    }
  };

  const cargarEstadisticas = async () => {
    try {
      const [generales, porVehiculo] = await Promise.all([
        axios.get(`${API_URL}/estadisticas/generales`),
        axios.get(`${API_URL}/estadisticas/todos-vehiculos`)
      ]);
      setEstadisticasGenerales(generales.data);
      setEstadisticasVehiculos(porVehiculo.data);
    } catch (error) {
      console.error('Error cargando estadísticas:', error);
    }
  };

  const cargarUltimoKilometraje = async (vehiculo_id) => {
    try {
      const response = await axios.get(`${API_URL}/vehiculos/${vehiculo_id}/ultimo-kilometraje`);
      if (response.data.kilometraje_final) {
        setFormData(prev => ({
          ...prev,
          kilometraje_inicial: response.data.kilometraje_final
        }));
      }
    } catch (error) {
      console.error('Error cargando último kilometraje:', error);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const username = e.target.username.value;
    const password = e.target.password.value;

    try {
      const response = await axios.post(`${API_URL}/login`, { username, password });
      setUsuario(response.data.usuario);
    } catch (error) {
      setError(error.response?.data?.error || 'Error al iniciar sesión');
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    setUsuario(null);
    setTabActiva('capturador');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (name === 'vehiculo_id' && value) {
      cargarUltimoKilometraje(value);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const dataToSend = {
        ...formData,
        usuario_id: usuario.id,
        vehiculo_id: parseInt(formData.vehiculo_id),
        kilometraje_inicial: parseInt(formData.kilometraje_inicial),
        kilometraje_final: parseInt(formData.kilometraje_final),
        costo_litro: parseFloat(formData.costo_litro),
        pago_total: parseFloat(formData.pago_total)
      };

      await axios.post(`${API_URL}/registros`, dataToSend);
      setSuccess('✅ Registro guardado exitosamente');
      
      setFormData({
        vehiculo_id: formData.vehiculo_id,
        fecha: new Date().toISOString().split('T')[0],
        kilometraje_inicial: formData.kilometraje_final,
        kilometraje_final: '',
        costo_litro: '',
        pago_total: '',
        gasolinera: 'UNO'
      });

      cargarRegistros();
      cargarEstadisticas();
      setTimeout(() => setSuccess(''), 3000);
    } catch (error) {
      setError(error.response?.data?.error || 'Error al guardar el registro');
    } finally {
      setLoading(false);
    }
  };

  const calcularValores = () => {
    const kmInicial = parseInt(formData.kilometraje_inicial) || 0;
    const kmFinal = parseInt(formData.kilometraje_final) || 0;
    const costoLitro = parseFloat(formData.costo_litro) || 0;
    const pagoTotal = parseFloat(formData.pago_total) || 0;

    const kmRecorridos = kmFinal - kmInicial;
    const litros = costoLitro > 0 ? pagoTotal / costoLitro : 0;
    const kmPorLitro = litros > 0 ? kmRecorridos / litros : 0;
    const costoPorKm = kmRecorridos > 0 ? pagoTotal / kmRecorridos : 0;
    const kmPorGalon = kmPorLitro * 3.78541;

    return {
      kmRecorridos: kmRecorridos >= 0 ? kmRecorridos : 0,
      litros: litros.toFixed(2),
      kmPorLitro: kmPorLitro.toFixed(2),
      costoPorKm: costoPorKm.toFixed(2),
      kmPorGalon: kmPorGalon.toFixed(2)
    };
  };

  if (!usuario) {
    return <LoginForm onLogin={handleLogin} error={error} loading={loading} />;
  }

  const valores = calcularValores();

  return (
    <div className="app-container">
      <Header usuario={usuario} onLogout={handleLogout} />
      
      <div className="tabs">
        <button 
          className={`tab ${tabActiva === 'capturador' ? 'active' : ''}`}
          onClick={() => setTabActiva('capturador')}
        >
          📝 Capturador
        </button>
        <button 
          className={`tab ${tabActiva === 'dashboard' ? 'active' : ''}`}
          onClick={() => setTabActiva('dashboard')}
        >
          📊 Dashboard
        </button>
        <button 
          className={`tab ${tabActiva === 'historial' ? 'active' : ''}`}
          onClick={() => setTabActiva('historial')}
        >
          📋 Historial
        </button>
      </div>

      {tabActiva === 'capturador' && (
        <Capturador 
          formData={formData}
          vehiculos={vehiculos}
          onChange={handleInputChange}
          onSubmit={handleSubmit}
          valores={valores}
          error={error}
          success={success}
          loading={loading}
        />
      )}

      {tabActiva === 'dashboard' && (
        <Dashboard 
          estadisticasGenerales={estadisticasGenerales}
          estadisticasVehiculos={estadisticasVehiculos}
          registros={registros}
        />
      )}

      {tabActiva === 'historial' && (
        <Historial registros={registros} vehiculos={vehiculos} />
      )}
    </div>
  );
}

function LoginForm({ onLogin, error, loading }) {
  return (
    <div className="login-container">
      <div className="login-card">
        <h1>🚗 Control de Combustible</h1>
        {error && <div className="error-message">{error}</div>}
        <form onSubmit={onLogin}>
          <div className="form-group">
            <label>Usuario</label>
            <input type="text" name="username" required autoComplete="username" />
          </div>
          <div className="form-group">
            <label>Contraseña</label>
            <input type="password" name="password" required autoComplete="current-password" />
          </div>
          <button type="submit" className="btn btn-primary" disabled={loading}>
            {loading ? 'Iniciando sesión...' : 'Iniciar Sesión'}
          </button>
        </form>
        <div style={{marginTop: '20px', textAlign: 'center', color: '#666', fontSize: '14px'}}>
          <p>Usuarios de prueba:</p>
          <p><strong>admin</strong> / admin123</p>
          <p><strong>user1</strong> / user123</p>
        </div>
      </div>
    </div>
  );
}

function Header({ usuario, onLogout }) {
  return (
    <div className="header">
      <h1>🚗 Sistema de Control de Combustible</h1>
      <div className="user-info">
        <span className="user-name">👤 {usuario.nombre}</span>
        <button onClick={onLogout} className="btn-logout">Cerrar Sesión</button>
      </div>
    </div>
  );
}

function Capturador({ formData, vehiculos, onChange, onSubmit, valores, error, success, loading }) {
  return (
    <div className="content-card">
      <h2>Registrar Carga de Combustible</h2>
      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}
      
      <form onSubmit={onSubmit}>
        <div className="form-grid">
          <div className="form-group">
            <label>Vehículo *</label>
            <select name="vehiculo_id" value={formData.vehiculo_id} onChange={onChange} required>
              <option value="">Seleccionar...</option>
              {vehiculos.map(v => (
                <option key={v.id} value={v.id}>{v.nombre} - {v.modelo}</option>
              ))}
            </select>
          </div>
          <div className="form-group">
            <label>Fecha *</label>
            <input type="date" name="fecha" value={formData.fecha} onChange={onChange} required />
          </div>
          <div className="form-group">
            <label>Kilometraje Inicial *</label>
            <input type="number" name="kilometraje_inicial" value={formData.kilometraje_inicial} onChange={onChange} required min="0" />
          </div>
          <div className="form-group">
            <label>Kilometraje Final *</label>
            <input type="number" name="kilometraje_final" value={formData.kilometraje_final} onChange={onChange} required min="0" />
          </div>
          <div className="form-group">
            <label>Costo por Litro *</label>
            <input type="number" step="0.01" name="costo_litro" value={formData.costo_litro} onChange={onChange} required min="0" />
          </div>
          <div className="form-group">
            <label>Pago Total *</label>
            <input type="number" step="0.01" name="pago_total" value={formData.pago_total} onChange={onChange} required min="0" />
          </div>
          <div className="form-group">
            <label>Gasolinera *</label>
            <select name="gasolinera" value={formData.gasolinera} onChange={onChange} required>
              {GASOLINERAS.map(g => (<option key={g} value={g}>{g}</option>))}
            </select>
          </div>
        </div>

        <div className="dashboard-grid" style={{marginTop: '30px'}}>
          <div className="stat-card">
            <h3>Kilómetros Recorridos</h3>
            <div className="value">{valores.kmRecorridos}</div>
            <div className="label">km</div>
          </div>
          <div className="stat-card">
            <h3>Litros</h3>
            <div className="value">{valores.litros}</div>
            <div className="label">litros</div>
          </div>
          <div className="stat-card">
            <h3>Km por Litro</h3>
            <div className="value">{valores.kmPorLitro}</div>
            <div className="label">km/lt</div>
          </div>
          <div className="stat-card">
            <h3>Costo por Km</h3>
            <div className="value">{valores.costoPorKm}</div>
            <div className="label">$/km</div>
          </div>
          <div className="stat-card">
            <h3>Km por Galón</h3>
            <div className="value">{valores.kmPorGalon}</div>
            <div className="label">km/gal</div>
          </div>
        </div>

        <button type="submit" className="btn btn-primary" style={{marginTop: '20px'}} disabled={loading}>
          {loading ? 'Guardando...' : '💾 Guardar Registro'}
        </button>
      </form>
    </div>
  );
}

function Dashboard({ estadisticasGenerales, estadisticasVehiculos, registros }) {
  if (!estadisticasGenerales || !estadisticasVehiculos) {
    return <div className="content-card">Cargando estadísticas...</div>;
  }

  const datosUltimos10 = registros.slice(0, 10).reverse().map(r => ({
    fecha: new Date(r.fecha).toLocaleDateString('es-HN', { month: 'short', day: 'numeric' }),
    kmLitro: parseFloat(r.km_por_litro.toFixed(2)),
    costoKm: parseFloat(r.costo_por_km.toFixed(2)),
    vehiculo: r.vehiculo_nombre
  }));

  const datosComparacion = estadisticasVehiculos.map(v => ({
    vehiculo: v.nombre,
    kmLitro: parseFloat((v.estadisticas.promedio_km_litro || 0).toFixed(2)),
    costoKm: parseFloat((v.estadisticas.promedio_costo_km || 0).toFixed(2)),
    totalGastado: parseFloat((v.estadisticas.total_gastado || 0).toFixed(2))
  }));

  return (
    <div>
      <div className="content-card" style={{marginBottom: '20px'}}>
        <h2>📊 Estadísticas Generales</h2>
        <div className="dashboard-grid">
          <div className="stat-card">
            <h3>Total Registros</h3>
            <div className="value">{estadisticasGenerales.total_registros || 0}</div>
            <div className="label">cargas realizadas</div>
          </div>
          <div className="stat-card">
            <h3>Total Kilómetros</h3>
            <div className="value">{(estadisticasGenerales.total_kilometros || 0).toLocaleString()}</div>
            <div className="label">km recorridos</div>
          </div>
          <div className="stat-card">
            <h3>Total Gastado</h3>
            <div className="value">L {(estadisticasGenerales.total_gastado || 0).toLocaleString('es-HN', {minimumFractionDigits: 2})}</div>
            <div className="label">lempiras</div>
          </div>
          <div className="stat-card">
            <h3>Total Litros</h3>
            <div className="value">{(estadisticasGenerales.total_litros || 0).toFixed(2)}</div>
            <div className="label">litros</div>
          </div>
          <div className="stat-card">
            <h3>Promedio Km/Litro</h3>
            <div className="value">{(estadisticasGenerales.promedio_km_litro || 0).toFixed(2)}</div>
            <div className="label">km/lt</div>
          </div>
          <div className="stat-card">
            <h3>Promedio Costo/Km</h3>
            <div className="value">{(estadisticasGenerales.promedio_costo_km || 0).toFixed(2)}</div>
            <div className="label">L/km</div>
          </div>
        </div>
      </div>

      <div className="content-card" style={{marginBottom: '20px'}}>
        <h2>🚗 Rendimiento por Vehículo</h2>
        <div className="vehicle-cards">
          {estadisticasVehiculos.map(vehiculo => (
            <div key={vehiculo.id} className="vehicle-card">
              <h3>{vehiculo.nombre}</h3>
              <p>{vehiculo.modelo}</p>
              <div className="vehicle-stats">
                <div className="vehicle-stat">
                  <div className="stat-label">Registros</div>
                  <div className="stat-value">{vehiculo.estadisticas.total_registros || 0}</div>
                </div>
                <div className="vehicle-stat">
                  <div className="stat-label">Total Km</div>
                  <div className="stat-value">{(vehiculo.estadisticas.total_kilometros || 0).toLocaleString()}</div>
                </div>
                <div className="vehicle-stat">
                  <div className="stat-label">Km/Litro</div>
                  <div className="stat-value">{(vehiculo.estadisticas.promedio_km_litro || 0).toFixed(2)}</div>
                </div>
                <div className="vehicle-stat">
                  <div className="stat-label">Total Gastado</div>
                  <div className="stat-value">L {(vehiculo.estadisticas.total_gastado || 0).toFixed(0)}</div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="chart-container">
        <h3>📈 Comparación de Rendimiento</h3>
        <ResponsiveContainer width="100%" height={300}>
          <BarChart data={datosComparacion}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="vehiculo" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Bar dataKey="kmLitro" fill="#667eea" name="Km/Litro" />
            <Bar dataKey="costoKm" fill="#764ba2" name="Costo/Km" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      {datosUltimos10.length > 0 && (
        <div className="chart-container">
          <h3>📉 Tendencia Últimas 10 Cargas</h3>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={datosUltimos10}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="fecha" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="kmLitro" stroke="#667eea" name="Km/Litro" strokeWidth={2} />
              <Line type="monotone" dataKey="costoKm" stroke="#764ba2" name="Costo/Km" strokeWidth={2} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      )}
    </div>
  );
}

function Historial({ registros, vehiculos }) {
  const [filtroVehiculo, setFiltroVehiculo] = useState('');
  const [filtroGasolinera, setFiltroGasolinera] = useState('');

  const registrosFiltrados = registros.filter(r => {
    const matchVehiculo = !filtroVehiculo || r.vehiculo_id == filtroVehiculo;
    const matchGasolinera = !filtroGasolinera || r.gasolinera === filtroGasolinera;
    return matchVehiculo && matchGasolinera;
  });

 return (
    <div className="content-card">
      <h2>📋 Historial de Cargas</h2>
      
      <div className="filters">
        {/* ... filtros ... */}
      </div>

      <div className="table-container">
        {/* ... tabla ... */}
      </div>

      {registrosFiltrados.length > 0 && (
        <div style={{marginTop: '20px', padding: '15px', background: '#f8f9fa', borderRadius: '10px'}}>
          <strong>Resumen: </strong>
          {registrosFiltrados.length} registro(s) encontrado(s) - 
          Total gastado: L {registrosFiltrados.reduce((sum, r) => sum + r.pago_total, 0).toFixed(2)} - 
          Total km: {registrosFiltrados.reduce((sum, r) => sum + r.kilometros_recorridos, 0).toLocaleString()}
        </div>
      )}
    </div>
  );
}

export default App;
